CREATE TABLE default.math_test (`dt` Float64, `sensor_id` String, `value_float` Float32, `loco_num` String, `section` String, `upload_id` String) ENGINE = MergeTree() PARTITION BY toMonday(toDateTime(dt)) ORDER BY (loco_num, section, upload_id, sensor_id, dt) SAMPLE BY dt SETTINGS index_granularity = 8192
